import { callAPI } from './callAPI'

const makeRequest = async (
  method,
  url,
  data,
  action,
  dispatch) => {
  try {
    const res = await callAPI(method, url, data);
    return await res.data;
  } catch (error) {

  }
}

export default makeRequest
